import React, {Component, useEffect, useState} from 'react';
import PropTypes from "prop-types";
import './styles.scss'


const Contact = ({ item, deleteContact }) => {
	const [isCompleted, setIsCompleted] = useState(false);

	useEffect(() => {
		setIsCompleted(item.isCompleted);
	}, [])

	const changeStatus = () => setIsCompleted(!isCompleted);
	const { name, key, id, surname, phone } = item;

	return (
		<tr className='contact contact__row' onClick={changeStatus} key={key}>
			<td className='contact__cell'>{name}</td>
			<td className='contact__cell'>{surname}</td>
			<td className='contact__cell'>{phone}</td>
			<td className='contact__cell contact_delete' onClick={() => deleteContact(id)}></td>
		</tr>
	);
}

Contact.propTypes = {
	deleteContact: PropTypes.func.isRequired,
	item: PropTypes.shape({
		name: PropTypes.string,
		surname: PropTypes.string.isRequired,
		phone: PropTypes.string.isRequired,
		key: PropTypes.string,
		id: PropTypes.number.isRequired,
	}),
};

Contact.defaultTypes = {
	contacts: [],
};

export default Contact;
